let firstTitle=document.getElementById("first-title");let knockout=document.getElementById("knockout");let body=document.body;let knockoutPosTop;let verticalBgSlider;const origPos=knockout.getBoundingClientRect().top;let vh=window.innerHeight;let vw=window.innerWidth;let bgOffsetY=400;let bgOffsetMakeUp=140;let vpAspect=function(){let viewportHeight=window.innerHeight;let viewportWidth=window.innerWidth;let aspect=viewportHeight/viewportWidth;if(aspect<.68){return true}else{return false}};window.addEventListener("load",function(e){if(vpAspect()){body.style.backgroundPositionY=-bgOffsetY+"px";window.addEventListener("scroll",function(e){knockoutPosTop=knockout.getBoundingClientRect().top;if(knockoutPosTop<=bgOffsetY&&knockoutPosTop>0){verticalBgSlider=knockoutPosTop;body.style.backgroundPositionY=-verticalBgSlider+bgOffsetMakeUp/verticalBgSlider+"px"}else if(window.pageYOffset>vh||knockoutPosTop==0){body.style.backgroundPositionY=bgOffsetMakeUp+"px"}})}else{body.style.backgroundPositionY=0}});window.addEventListener("resize",function(e){if(vpAspect()){body.style.backgroundPositionY=-bgOffsetY+"px";window.addEventListener("scroll",function(e){knockoutPosTop=knockout.getBoundingClientRect().top;if(knockoutPosTop<=bgOffsetY&&knockoutPosTop>0){verticalBgSlider=knockoutPosTop;body.style.backgroundPositionY=-verticalBgSlider+bgOffsetMakeUp/verticalBgSlider+"px"}else if(window.pageYOffset>vh||knockoutPosTop==0){body.style.backgroundPositionY=bgOffsetMakeUp+"px"}})}else{body.style.backgroundPositionY=0}});window.addEventListener("scroll",function(){knockoutPosTop=knockout.getBoundingClientRect().top;if(knockoutPosTop<=0){firstTitle.classList.add("animate-up");knockout.classList.add("freeze-knockout")}else if(knockoutPosTop>0&&knockout.classList.contains("freeze-knockout")){knockout.classList.remove("freeze-knockout")}else if(knockoutPosTop>=origPos){firstTitle.classList.remove("animate-up")}});document.fonts.ready.then(function(){var titleContent=document.getElementById("hide-title-load");titleContent.style.visibility="visible"});document.fonts.ready.then(function(){var statContent=document.getElementById("hide-clar-load");statContent.style.visibility="visible"});
